import React from "react";

function article_1() {
	return {
		date: "7 May 2023",
		title: "The Benefits of Cloud Computing",
		description:
			"Cloud computing offers a range of benefits, including cost savings and increased flexibility. Find out why more businesses are turning to the cloud.",
		keywords: [
			"The Benefits of Cloud Computing",
			"Tharindu",
			"Tharindu N",
			"Tharindu Nayanajith",
		],
		style: `
				.article-content {
					display: flex;
					flex-direction: column;
					align-items: center;
				}

				.randImage {
					align-self: center;
					outline: 2px solid red;
				}
				`,
		body: (
			<React.Fragment>
				<div className="article-content">
					<div className="paragraph">Content of article 1</div>
					<img
						src="https://picsum.photos/200/300"
						alt="random"
						className="randImage"
					/>
				</div>
			</React.Fragment>
		),
	};
}


function airpollution() {
	return {
		date: "8 July 2024",
		title: "Air Pollution and Economic Development in Colombia",
		description:
			"Explore the intricate relationship between air pollution and economic development in Colombia through the lens of the Environmental Kuznets Curve (EKC) hypothesis.",
		keywords: [
			"Air Pollution and Economic Development",
			"Environmental Kuznets Curve",
			"EKC",
			"Colombia",
		],
		style: `
				.article-content {
					display: flex;
					flex-direction: column;
					align-items: center;
				}

				.randImage {
					align-self: center;
					outline: 2px solid blue;
				}
				`,
		body: (
			<React.Fragment>
				<div className="article-content">
					<h2>Intro/Hook</h2>
					<p>
						The Environmental Kuznets Curve (EKC) provides a compelling framework for understanding the intricate relationship between economic growth and environmental degradation. According to the EKC hypothesis, as a nation begins to develop economically, environmental degradation initially increases. London showed an inverted-U shape relationship between air pollution and economic development, showing the increase until peaking at 1891, and the decreasing trend from onward.
					</p>
					<p>
						<ul>
							<li>Can the same inverted-U shape be observed in developing countries as in London?</li>
							<li>In the United States, air pollution disproportionately affects disadvantaged populations. Do we see the same environmental justice issues in Colombia?</li>
						</ul>
					</p>
					<a
						href="../airpollution//Poster%20Assets/Air%20Pollution%20vs%20GDP%20Per%20Capita%20in%20London%20(1700-2015).html"
						target="_blank"
						rel="noopener noreferrer"
					>
						Air Pollution vs GDP Per Capita in London (1700-2015)
					</a>
					<img
						src="https://picsum.photos/200/300"
						alt="random"
						className="randImage"
					/>
				</div>
			</React.Fragment>
		),
	};
}

const myArticles = [article_1, airpollution];

export default myArticles;
